pragma solidity ^0.8.13;

import {IConnext} from "@connext/interfaces/core/IConnext.sol";
import {IXReceiver} from "@connext/interfaces/core/IXReceiver.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./MeanFinanceAdapter.sol";
import "../Uniswap/UniswapAdapter.sol";

contract MeanFinanceTarget is MeanFinanceAdapter, UniswapAdapter {
    // The Connext contract on this domain
    IConnext public immutable connext;

    receive()
        external
        payable
        virtual
        override(MeanFinanceAdapter, UniswapAdapter)
    {}

    /// Modifier
    modifier onlyConnext() {
        require(msg.sender == address(connext), "Caller must be Connext");
        _;
    }

    constructor(address _connext) {
        connext = IConnext(_connext);
    }

    function xReceive(
        bytes32 _transferId,
        uint256 _amount, // Final Amount receive via Connext(After AMM calculation)
        address _asset,
        address _originSender,
        uint32 _origin,
        bytes calldata _callData
    ) external returns (bytes memory) {
        uint256 amount = _amount;
        // Decode calldata
        (
            uint24 poolFee,
            uint256 amountOutMin,
            address from,
            address to,
            uint32 amountOfSwaps,
            uint32 swapInterval,
            address owner,
            IDCAPermissionManager.PermissionSet[] memory permissions
        ) = decode(_callData);

        require(amount > 0, "!amount");
        require(amountOutMin > 0, "!amountOut");
        require(from != address(0), "!invalid");

        if (from != _asset) {
            // swap to deposit asset if needed
            amount = swap(_asset, from, poolFee, amount, amountOutMin);
        }

        // deposit
        deposit(
            from,
            to,
            amount,
            amountOfSwaps,
            swapInterval,
            owner,
            permissions
        );
    }

    function encode(
        uint24 poolFee,
        uint256 amountOutMin,
        address from,
        address to,
        uint32 amountOfSwaps,
        uint32 swapInterval,
        address owner,
        IDCAPermissionManager.PermissionSet[] memory permissions
    ) external pure returns (bytes memory) {
        return
            abi.encode(
                poolFee,
                amountOutMin,
                from,
                to,
                amountOfSwaps,
                swapInterval,
                owner,
                permissions
            );
    }

    /// INTERNAL
    function decode(
        bytes calldata data
    )
        internal
        pure
        returns (
            uint24 poolFee,
            uint256 amountOutMin,
            address from,
            address to,
            uint32 amountOfSwaps,
            uint32 swapInterval,
            address owner,
            IDCAPermissionManager.PermissionSet[] memory permissions
        )
    {
        (
            poolFee,
            amountOutMin,
            from,
            to,
            amountOfSwaps,
            swapInterval,
            owner,
            permissions
        ) = abi.decode(
            data,
            (
                uint24,
                uint256,
                address,
                address,
                uint32,
                uint32,
                address,
                IDCAPermissionManager.PermissionSet[]
            )
        );
    }
}
","keccak256":"0x6e85a1aaefd56c7138b1d3109734227e031307b701d42af1e89b3fd6562e82e0","license":"UNLICENSED"},"contracts/Uniswap/UniswapAdapter.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {ISwapRouter} from "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import {TransferHelper} from "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

contract UniswapAdapter {
    /// @notice UniswapV3 swap router contract to swap
    /// @dev If deploying to celo, change hardcoded address. see https://docs.uniswap.org/contracts/v3/reference/deployments
    ISwapRouter public immutable swapRouter =
        ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);

    /// Payable
    receive() virtual external payable {}

    function swap(
        address fromAsset,
        address toAsset,
        uint24 poolFee,
        uint256 amountIn,
        uint256 amountOutMin
    ) public returns (uint256 amountOut) {
        // Approve the uniswap router to spend fromAsset.
        TransferHelper.safeApprove(fromAsset, address(swapRouter), amountIn);
        // Set up uniswap swap params.
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
            .ExactInputSingleParams({
                tokenIn: fromAsset,
                tokenOut: toAsset,
                fee: poolFee,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                amountOutMinimum: amountOutMin,
                sqrtPriceLimitX96: 0
            });

        // The call to `exactInputSingle` executes the swap.
        amountOut = swapRouter.exactInputSingle(params);
    }
}
